function kd2u3(startName, endName, convMat)  startImage = imread(startName);  grayImage = rgb2gray(startImage);  res = conv2(grayImage, convMat, 'same');  imwrite(res, endName);endfunction#Dot? testa matricatest = [-4,-2,-1,-2,-4;        -2,-1,0,-1,-2;       -1,0,40,0,1 ;       -2,-1,0,-1,-2;       -4,-2,-1,-2,-4;]     startN = "00sl-dnaprivacy-superJumbo.jpg";     endN = "DONE_00sl-dnaprivacy-superJumbo.jpg";kd2u3(startN, endN, test);